// src/components/InventoryList.jsx
import React, { useState } from 'react';
import mockInventory from '../../data/mockInventory';
import ProductCard from '../ProductCard/ProductCard';
import ProductFormModal from '../ProductFormModal/ProductFormModal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faPlus } from '@fortawesome/free-solid-svg-icons';
import './inventorylist.css';

const InventoryList = () => {
  const [inventory, setInventory] = useState(mockInventory);
  const [searchTerm, setSearchTerm] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentProduct, setCurrentProduct] = useState({ id: null, name: '', description: '', price: 0, quantity: 0, imageUrl: '' });

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredInventory = inventory.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDelete = (id) => {
    setInventory(inventory.filter(item => item.id !== id));
  };

  const handleEdit = (id) => {
    const productToEdit = inventory.find(item => item.id === id);
    setCurrentProduct(productToEdit);
    setIsModalOpen(true);
  };

  const handleAdd = () => {
    setCurrentProduct({ id: null, name: '', description: '', price: 0, quantity: 0, imageUrl: '' });
    setIsModalOpen(true);
  };

  const handleSave = (product) => {
    if (product.id) {
      setInventory(inventory.map(item => (item.id === product.id ? product : item)));
    } else {
      setInventory([...inventory, { ...product, id: inventory.length + 1 }]);
    }
    setIsModalOpen(false);
  };

  return (
    <div className="inventory-container">
      <h1>Products</h1>
      <div className="inventory-controls">
        <div className="search-bar">
          <FontAwesomeIcon icon={faSearch} />
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <button className="add-button" onClick={handleAdd}>
          <FontAwesomeIcon icon={faPlus} /> Add New Product
        </button>
      </div>
      <div className="inventory-list">
        {filteredInventory.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>
      <ProductFormModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={handleSave}
        initialProduct={currentProduct}
      />
    </div>
  );
};

export default InventoryList;
