// src/components/InventoryList.jsx
import React, { useState } from 'react';
import mockInventory from '../../data/mockInventory';
import ProductCard from './ProductCard';
import './inventorylist.css';

const InventoryList = () => {
  const [inventory, setInventory] = useState(mockInventory);
  const [searchTerm, setSearchTerm] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [currentProduct, setCurrentProduct] = useState({ id: null, name: '', description: '', price: 0, quantity: 0 });

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredInventory = inventory.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDelete = (id) => {
    setInventory(inventory.filter(item => item.id !== id));
  };

  const handleEdit = (id) => {
    const productToEdit = inventory.find(item => item.id === id);
    setIsEditing(true);
    setCurrentProduct(productToEdit);
  };

  const handleAddEditProduct = () => {
    if (isEditing) {
      setInventory(inventory.map(item => (item.id === currentProduct.id ? currentProduct : item)));
    } else {
      setInventory([...inventory, { ...currentProduct, id: inventory.length + 1 }]);
    }
    setIsEditing(false);
    setCurrentProduct({ id: null, name: '', description: '', price: 0, quantity: 0 });
  };

  return (
    <div>
      <h1>Inventory</h1>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="inventory-form">
        <input
          type="text"
          placeholder="Name"
          value={currentProduct.name}
          onChange={(e) => setCurrentProduct({ ...currentProduct, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={currentProduct.description}
          onChange={(e) => setCurrentProduct({ ...currentProduct, description: e.target.value })}
        />
        <input
          type="number"
          placeholder="Price"
          value={currentProduct.price}
          onChange={(e) => setCurrentProduct({ ...currentProduct, price: Number(e.target.value) })}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={currentProduct.quantity}
          onChange={(e) => setCurrentProduct({ ...currentProduct, quantity: Number(e.target.value) })}
        />
        <button onClick={handleAddEditProduct}>
          {isEditing ? 'Update Product' : 'Add Product'}
        </button>
      </div>
      <div className="inventory-list">
        {filteredInventory.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>
    </div>
  );
};

export default InventoryList;
